mapper.select:
SELECT a.cdgSeq , a.cdgName , a.cdgRegDt , a.cdgUdtDt , a.cdgDelNy , (SELECT COUNT(*) FROM code aa WHERE aa.cdcSeq = a.cdgSeq ) xcodeCount FROM codegroup a WHERE 1=1 AND a.cdgDelNy = #{shDelNy}
AND a.cdgRegDt BETWEEN #{shDateStart} AND #{shDateEnd} AND a.cdgUdtDt BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.cdgSeq = #{shValue} AND a.cdgName LIKE CONCAT('%', #{shValue}, '%')
ORDER BY a.cdgSeq DESC

mapper.update:
	UPDATE commongroup
	   SET name = #{name}
	     , modDateTime = now()
     WHERE 1=1
       AND seq = #{seq}

mapper.delete:
	DELETE FROM commongroup
     WHERE 1=1
       AND seq = #{seq}
