mapper.sql:
 FROM codegroup a
WHERE 1=1
AND a.cdgDelNy = #{shDelNy}


	AND a.cdgRegDt BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.cdgUdtDt BETWEEN #{shDateStart} AND #{shDateEnd}



	AND a.cdgSeq = #{shValue}
	AND a.cdgName LIKE CONCAT('%', #{shValue}, '%')


AND a.cdgSeq = #{shOptionGroup}

mapper.update:
	UPDATE codegroup
	   SET cdgName  = #{cdgName}
	     , cdgUdtDt = now()
     WHERE 1=1
       AND cdgSeq = #{cdgSeq}

UPDATE codegroup SET cdgUdtDt = now()
, cdgDelNy = 1 , cdgDelNy = 0
WHERE 1 = 1 AND cdgSeq = #{cdgSeq}

mapper.delete:
	DELETE FROM codegroup
     WHERE 1=1
       AND cdgSeq = #{cdgSeq}
