mapper.sql:
		  FROM codegroup a
     		   LEFT JOIN code b
            		  ON b.cdgSeq = a.cdgSeq 
       WHERE 1 = 1          

       AND IFNULL(b.cdcDelNy, 0) = #{shDelNy}


       	AND b.cdcRegDt BETWEEN #{shDateStart} AND #{shDateEnd}
       	AND b.cdcUdtDt BETWEEN #{shDateStart} AND #{shDateEnd}



       	AND a.cdgName LIKE CONCAT('%', #{shValue}, '%')
       	AND a.cdgSeq = #{shValue}
       	AND b.cdcName LIKE CONCAT('%', #{shValue}, '%')
       	AND b.cdcSeq = #{shValue}


       AND a.cdgSeq = #{shOptionGroup}

mapper.update:
		UPDATE code
		   SET cdcName  = #{cdcName}
		     , cdcUdtDt = now()
         WHERE 1 = 1		
           AND cdcSeq = #{cdcSeq}

UPDATE code SET cdcUdtDt = now()
, cdcDelNy = 1 , cdcDelNy = 0
WHERE 1 = 1 AND cdcSeq = #{cdcSeq}

mapper.delete:
		DELETE FROM code
        WHERE 1 = 1		
          AND cdcSeq = #{cdcSeq}
