mapper.select:
SELECT a.cdgSeq , a.cdgName , b.cdcDelNy , b.cdcSeq , b.cdcName , b.cdcRegDt , b.cdcUdtDt FROM codegroup a LEFT JOIN code b ON b.cdgSeq = a.cdgSeq WHERE 1 = 1
AND b.cdcDelNy = #{shDelNy}
AND b.cdcRegDt BETWEEN #{shDateStart} AND #{shDateEnd} AND b.cdcUdtDt BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.cdgSeq = #{shValue} AND a.cdgName LIKE CONCAT('%', #{shValue}, '%') AND b.cdcSeq = #{shValue} AND b.cdcName LIKE CONCAT('%', #{shValue}, '%')
ORDER BY a.cdgSeq, b.cdcSeq

mapper.update:
		UPDATE code
		   SET cdcName  = #{cdcName}
		     , cdcUdtDt = now()
         WHERE 1 = 1		
           AND cdcSeq = #{cdcSeq}

mapper.delete:
		DELETE FROM code
        WHERE 1 = 1		
          AND cdcSeq = #{cdcSeq}
