mapper.insert:
INSERT INTO fileuploaded
	( 
	  category
	, defaultNy
	, sort
	, pathName
	, path
	, originalName
	, uuidName
	, ext
	, size
	, delNy
	, pseq
	, regDateTime
	)
VALUES
	(
	  #{category}
	, #{defaultNy}
	, #{sort}
	, #{pathName}
	, #{path}
	, #{originalName}
	, #{uuidName}
	, #{ext}
	, #{size}
	, 0
	, #{pseq}
	, now()
	)

mapper.update:
UPDATE fileuploaded
   SET defaultNy   = 1
     , regDateTime = now()
 WHERE category  = #{category}
   AND pseq      = #{pseq}
   AND defaultNy = 0

UPDATE fileuploaded
   SET uuidName   = #{uuidName}
     , regDateTime = now()
 WHERE category  = #{category}
   AND pseq      = #{pseq}
   AND defaultNy = 0

mapper.select:
SELECT uuidName
  FROM fileuploaded
 WHERE category = #{category}
   AND pseq     = #{pseq}
