mapper.sql:
  FROM members a
 WHERE 1 = 1
   AND (@rownum:=0) = 0

	AND a.mbrRegDt BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.mbrUdtDt BETWEEN #{shDateStart} AND #{shDateEnd}



	AND a.mbrName  LIKE CONCAT('%', #{shValue}, '%')
	AND a.mbrEmail LIKE CONCAT('%', #{shValue}, '%')


AND a.mbrType = #{shOptionType}

AND a.mbrDelNy = #{shDelNy}

AND a.mbrSex = #{shSex}

mapper.insert:
INSERT INTO members
	( 
	  mbrType
	, mbrName
	, mbrSex
	, mbrBirthday
	, mbrEmail
	, mbrPassword
	, mbrZipCode
	, mbrAddr
	, mbrAddrD
	, mbrAddrRemark
	, mbrPointLat
	, mbrPointLon
	, mbrRegDt
	, mbrUdtDt
	, mbrDelNy
	)
VALUES
	(
	  #{mbrType}
	, #{mbrName}
	, #{mbrSex}
	, STR_TO_DATE(#{mbrBirthday},'%Y-%m-%d %H%i%s')
	, #{mbrEmail}
	, #{mbrPassword}
	, #{mbrZipCode}
	, #{mbrAddr}
	, #{mbrAddrD}
	, #{mbrAddrRemark}
	, #{mbrPointLat}
	, #{mbrPointLon}
	, now()
	, now()
	, 0
	)

	SELECT LAST_INSERT_ID()

mapper.update:
		UPDATE members
		   SET mbrName       = #{mbrName}
			 , mbrSex        = #{mbrSex}
			 , mbrZipCode    = #{mbrZipCode}
			 , mbrAddr       = #{mbrAddr}
			 , mbrAddrD      = #{mbrAddrD}
			 , mbrAddrRemark = #{mbrAddrRemark}
			 , mbrPointLat   = #{mbrPointLat}
			 , mbrPointLon   = #{mbrPointLon}
			 , mbrBirthday = STR_TO_DATE(#{mbrBirthday},'%Y-%m-%d %H%i%s')
			 , mbrUdtDt = now()
         WHERE 1 = 1		
           AND mbrSeq = #{mbrSeq}

		UPDATE members
		   SET mbrPassword = #{mbrPassword}
		     , mbrUdtDt = now()
         WHERE 1 = 1		
           AND mbrSeq = #{mbrSeq}

UPDATE members SET mbrUdtDt = now()
, mbrDelNy = 1 , mbrDelNy = 0
WHERE 1 = 1 AND mbrSeq = #{mbrSeq}

mapper.delete:
		DELETE FROM members
         WHERE 1 = 1		
           AND mbrSeq = #{mbrSeq}
