mapper.select:
SELECT a.mbrSeq , a.mbrType , a.mbrName , a.mbrSex , b.cdcName xsexName , a.mbrBirthday , a.mbrEmail , a.mbrPassword , a.mbrRegDt , a.mbrUdtDt , a.mbrDelNy FROM members a LEFT JOIN code b ON b.cdcSeq = a.mbrSex WHERE 1 = 1
AND a.mbrRegDt BETWEEN #{shDateStart} AND #{shDateEnd} AND a.mbrUdtDt BETWEEN #{shDateStart} AND #{shDateEnd}
AND a.mbrName LIKE CONCAT('%', #{shValue}, '%') AND a.mbrEmail LIKE CONCAT('%', #{shValue}, '%')
AND a.mbrType = #{shOptionType}
AND a.mbrDelNy = #{shDelNy}
AND a.mbrSex = #{shSex}
ORDER BY a.mbrSeq DESC

mapper.update:
		UPDATE members
		   SET mbrName     = #{mbrName}
			 , mbrSex      = #{mbrSex}

			 	, mbrBirthday = STR_TO_DATE(#{mbrBirthday},'%Y-%m-%d %H%i%s')
			 	, mbrBirthday = null

			 , mbrEmail    = #{mbrEmail}
			 , mbrPassword = #{mbrPassword}
         WHERE 1 = 1		
           AND mbrSeq = #{mbrSeq}

		UPDATE members
		   SET mbrPassword = #{mbrPassword}
         WHERE 1 = 1		
           AND mbrSeq = #{mbrSeq}

UPDATE members SET mbrUdtDt = now()
, mbrDelNy = 1 , mbrDelNy = 0
WHERE 1 = 1 AND mbrSeq = #{mbrSeq}

mapper.delete:
		DELETE FROM members
         WHERE 1 = 1		
           AND mbrSeq = #{mbrSeq}
