mapper.sql:
  FROM product a
 WHERE 1 = 1


	AND a.pdtRegDt BETWEEN #{shDateStart} AND #{shDateEnd}
	AND a.pdtUdtDt BETWEEN #{shDateStart} AND #{shDateEnd}



	AND a.pdtName LIKE CONCAT('%', #{shValue}, '%')



	AND a.pdtRunKm  BETWEEN #{shOptionRunKmFrom} AND #{shOptionRunKmTo}
	AND a.pdtRunKm <= #{shOptionRunKmFrom}
	AND a.pdtRunKm >= #{shOptionRunKmFrom}



	AND a.pdtYear  BETWEEN #{shOptionYearFrom} AND #{shOptionYearTo}
	AND a.pdtYear <= #{shOptionYearFrom}
	AND a.pdtYear >= #{shOptionYearFrom}



	AND a.pdtMonth  BETWEEN #{shOptionMonthFrom} AND #{shOptionMonthTo}
	AND a.pdtMonth <= #{shOptionMonthFrom}
	AND a.pdtMonth >= #{shOptionMonthFrom}


  AND a.pdtTypeCd      = #{shCarType}
 AND a.pdtBrandCd     = #{shCarBrand}
 AND a.pdtColorCd     = #{shCarColor}
AND a.pdtRecommendCd = #{shRecommend}
 AND a.pdtAccidentCd  = #{shAccident}
    AND a.pdtDelNy       = #{shDelNy}

mapper.select:
SELECT * FROM ( SELECT a.pdtSeq , a.pdtTypeCd , d.cdcName xpdtTypeName , a.pdtBrandCd , CONCAT('[', e.cdcName, ']') xpdtBrandName , a.pdtName , a.pdtAccidentCd , f.cdcName xpdtAccidentName , a.pdtYear , a.pdtMonth , a.pdtRunKm , a.pdtColorCd , g.cdcName xpdtColorName , a.pdtPrice , a.pdtRecommendCd , MAX(CASE WHEN b.pdiBack = 0 THEN b.pdiUrl END) xpdtImgF , MAX(CASE WHEN b.pdiBack = 1 THEN b.pdiUrl END) xpdtImgB , IFNULL(c.xpdtStar ,0) xpdtStar , IFNULL(c.xpdtCount,0) xpdtCount
, h.wshSeq xwshSeq , null xwshSeq
FROM product a LEFT JOIN productimg b ON b.pdtSeq = a.pdtSeq LEFT JOIN (SELECT pdtSeq , ROUND(AVG(rvwStar)) xpdtStar , COUNT(*) xpdtCount FROM productreview GROUP BY pdtSeq ) c ON c.pdtSeq = a.pdtSeq JOIN code d ON d.cdcSeq = a.pdtTypeCd JOIN code e ON e.cdcSeq = a.pdtBrandCd JOIN code f ON f.cdcSeq = a.pdtAccidentCd JOIN code g ON g.cdcSeq = a.pdtColorCd
LEFT JOIN productWishlist h ON h.pdtSeq = a.pdtSeq AND h.mbrSeq = #{shMbrSeq}
WHERE 1 = 1 AND a.pdtDelNy = 0 AND a.pdtSeq = #{shSeq} AND a.pdtRecommendCd = #{shRecommend}
AND a.pdtTypeCd IN
#{type}
AND a.pdtBrandCd IN
#{brand}
AND a.pdtColorCd IN
#{color}
AND a.pdtYear BETWEEN #{shOptionYearFrom} AND #{shOptionYearTo}
AND a.pdtPrice BETWEEN #{shPriceFrom} AND #{shPriceTo}
GROUP BY a.pdtSeq , a.pdtTypeCd , d.cdcName , a.pdtBrandCd , CONCAT('[', e.cdcName, ']') , a.pdtName , a.pdtAccidentCd , f.cdcName , a.pdtYear , a.pdtMonth , a.pdtRunKm , a.pdtColorCd , g.cdcName , a.pdtPrice , a.pdtRecommendCd , h.wshSeq ORDER BY a.pdtSeq LIMIT #{shDispCount} ) a LIMIT #{pgRowCount} OFFSET #{pgOffset}

mapper.insert:
INSERT INTO product
	( 
	  pdtTypeCd
	, pdtBrandCd
	, pdtName
	, pdtAccidentCd
	, pdtYear
	, pdtMonth
	, pdtRunKm
	, pdtColorCd
	, pdtPrice
	, pdtRecommendCd
	, pdtRegDt
	, pdtUdtDt
	, pdtDelNy
	)
VALUES
	(
	  #{pdtTypeCd}
	, #{pdtBrandCd}
	, #{pdtName}
	, #{pdtAccidentCd}
	, #{pdtYear}
	, #{pdtMonth}
	, #{pdtRunKm}
	, #{pdtColorCd}
	, #{pdtPrice}
	, #{pdtRecommendCd}
	, now()
	, now()
	, 0
	)

mapper.update:
		UPDATE product
		   SET pdtTypeCd      = #{pdtTypeCd}
			 , pdtBrandCd     = #{pdtBrandCd}
			 , pdtName        = #{pdtName}
			 , pdtAccidentCd  = #{pdtAccidentCd}
			 , pdtYear        = #{pdtYear}
			 , pdtMonth       = #{pdtMonth}
			 , pdtRunKm       = #{pdtRunKm}
			 , pdtColorCd     = #{pdtColorCd}
			 , pdtPrice       = #{pdtPrice}
			 , pdtRecommendCd = #{pdtRecommendCd}
			 , pdtUdtDt       = now()
         WHERE 1 = 1		
           AND pdtSeq = #{pdtSeq}

UPDATE product SET pdtUdtDt = now()
, pdtDelNy = 1 , pdtDelNy = 0
WHERE 1 = 1 AND pdtSeq = #{pdtSeq}

mapper.delete:
		DELETE FROM product
         WHERE 1 = 1		
           AND pdtSeq = #{pdtSeq}
